    vertex_values[4] = (1.0/detJ)*(dof_values[0]*3*J_10 + dof_values[1]*(-3*J_10) + dof_values[2]*J_10 + dof_values[6]*(J_10 - J_11) + dof_values[7]*(-3*J_10 + 3*J_11) + dof_values[8]*(3*J_10 - 3*J_11));
