/// This class defines the interface for the tabulation of the cell
/// tensor corresponding to the local contribution to a form from
/// the integral over a cell.

class velocityp1projection_1_cell_integral_0_quadrature: public ufc::cell_integral
{
public:

  /// Constructor
  velocityp1projection_1_cell_integral_0_quadrature() : ufc::cell_integral()
  {
    // Do nothing
  }

  /// Destructor
  virtual ~velocityp1projection_1_cell_integral_0_quadrature()
  {
    // Do nothing
  }

  /// Tabulate the tensor for the contribution from a local cell
  virtual void tabulate_tensor(double* A,
                               const double * const * w,
                               const ufc::cell& c) const
  {
    // Extract vertex coordinates
    const double * const * x = c.coordinates;
    
    // Compute Jacobian of affine map from reference cell
    const double J_00 = x[1][0] - x[0][0];
    const double J_01 = x[2][0] - x[0][0];
    const double J_10 = x[1][1] - x[0][1];
    const double J_11 = x[2][1] - x[0][1];
    
    // Compute determinant of Jacobian
    double detJ = J_00*J_11 - J_01*J_10;
    
    // Compute inverse of Jacobian
    
    // Set scale factor
    const double det = std::abs(detJ);
    
    
    // Array of quadrature weights
    static const double W4[4] = {0.159020690871988, 0.0909793091280113, 0.159020690871988, 0.0909793091280113};
    // Quadrature points on the UFC reference element: (0.178558728263616, 0.155051025721682), (0.0750311102226081, 0.644948974278318), (0.666390246014701, 0.155051025721682), (0.280019915499074, 0.644948974278318)
    
    // Value of basis functions at quadrature points.
    static const double FE0_C0[4][6] = \
    {{0.666390246014701, 0.178558728263616, 0.155051025721682, 0, 0, 0},
    {0.280019915499074, 0.0750311102226082, 0.644948974278318, 0, 0, 0},
    {0.178558728263616, 0.666390246014701, 0.155051025721682, 0, 0, 0},
    {0.0750311102226081, 0.280019915499074, 0.644948974278318, 0, 0, 0}};
    
    static const double FE0_C1[4][6] = \
    {{0, 0, 0, 0.666390246014701, 0.178558728263616, 0.155051025721682},
    {0, 0, 0, 0.280019915499074, 0.0750311102226082, 0.644948974278318},
    {0, 0, 0, 0.178558728263616, 0.666390246014701, 0.155051025721682},
    {0, 0, 0, 0.0750311102226081, 0.280019915499074, 0.644948974278318}};
    
    static const double FE1_C0[4][15] = \
    {{-0.34437686815613, 0.16176870638247, -0.114792289385378, 0.512663596846696, -0.31573400198994, -0.0534944393051878, -0.160444329828792, 0.43568094904296, -0.298724827712715, 0.666242174054459, -0.202046936762163, -0.866186565811894, 0, 0, 0},
    {-0.191315325660401, 0.246077390525797, -0.0637717752201335, -0.168932927257231, 0.419657050444072, 0.424020028137765, -0.0500812590037847, 0.163934293227703, -0.20500584187675, 0.192988310486867, -0.16618391787544, 0.259759281522801, 0, 0, 0},
    {0.665285021857101, -0.696615629217421, 0.221761673952367, -0.4591789045632, 0.315734001989938, -0.427867791246268, 0.213929022112287, -0.649619718176941, 0.673117673697181, 0.797252379198796, -0.428963268382175, -0.14861411960257, 0, 0, 0},
    {-0.369592828040569, 0.688769532309152, -0.123197609346855, 0.0754482349737343, -0.419657050444068, 0.417342202413689, -0.0434034332797102, 0.210004475906278, -0.449387004107715, 0.421978105342532, -0.562805876980222, 1.51398804233207, 0, 0, 0}};
    
    static const double FE1_C1[4][15] = \
    {{-0.10696938456699, 0.169630599287629, -0.320908153700971, 0.144788773170325, -0.396546930907641, 0.283088765097635, -0.51267334386839, 0.182608161773661, 0.0847958056004274, 0.131010205144337, 0.599679597784017, 0.717572446209324, 0, 0, 0},
    {0.186969384566992, -0.825322793104164, 0.560908153700974, -0.120865724716194, 0.42870083399938, -0.627011813551772, 0.499468263111502, -0.0547620648653964, 0.248409275156462, 0.228989794855664, 1.04816649912425, 1.25422876080927, 0, 0, 0},
    {-0.106969384566991, 0.472185708114315, -0.320908153700973, 0.0691499959636559, -0.245269376494303, 0.358727542304309, 0.0847958056004266, 0.0313306073603201, -0.51267334386839, -0.131010205144337, 0.448402043370674, -0.717572446209324, 0, 0, 0},
    {0.186969384566989, -0.29649351429778, 0.56090815370097, -0.253073044417787, 0.693115473402564, -0.494804493850172, 0.248409275156461, -0.319176704268585, 0.4994682631115, -0.228989794855664, 0.783751859721058, -1.25422876080927, 0, 0, 0}};
    
    
    // Compute element tensor using UFL quadrature representation
    // Optimisations: ('simplify expressions', False), ('ignore zero tables', False), ('non zero columns', False), ('remove zero terms', False), ('ignore ones', False)
    // Total number of operations to compute element tensor: 624
    
    // Loop quadrature points for integral
    // Number of operations to compute element tensor for following IP loop = 624
    for (unsigned int ip = 0; ip < 4; ip++)
    {
      
      // Function declarations
      double F0 = 0;
      double F1 = 0;
      
      // Total number of operations to compute function values = 60
      for (unsigned int r = 0; r < 15; r++)
      {
        F0 += FE1_C0[ip][r]*w[0][r];
        F1 += FE1_C1[ip][r]*w[0][r];
      }// end loop over 'r'
      
      // Number of operations for primary indices: 96
      for (unsigned int j = 0; j < 6; j++)
      {
        // Number of operations to compute entry: 16
        A[j] += (FE0_C1[ip][j]*((1.0/detJ)*J_10*F0 + (1.0/detJ)*J_11*F1) + FE0_C0[ip][j]*((1.0/detJ)*J_00*F0 + (1.0/detJ)*J_01*F1))*W4[ip]*det;
      }// end loop over 'j'
    }// end loop over 'ip'
  }

};

